import Item from "./Item";
import "./Items.css";
import { useState, useEffect } from "react";

const Items = (props) => {
  const [items, setItems] = useState([]);

  useEffect(() => {
    const fetchedItems = [
      {
        id: "1",
        name: "Cat",
        description: "Cute and fluffy",
        price: "2000",
        image:
          "https://static01.nyt.com/images/2021/09/14/science/07CAT-STRIPES/07CAT-STRIPES-mediumSquareAt3X-v2.jpg",
        size: "small",
      },
      {
        id: "2",
        name: "My phone",
        description: "Iphone 13",
        price: "1000",
        image:
          "https://www.techniknews.net/wp-content/uploads/2021/10/iphone-13-pro-testbericht-header-scaled.jpg",
        size: "small",
      },
      {
        id: "3",
        name: "Dog",
        description: "Very active and friendly",
        price: "3000",
        image:
          "https://media-cldnry.s-nbcnews.com/image/upload/rockcms/2022-08/220805-border-collie-play-mn-1100-82d2f1.jpg",
        size: "medium",
      },
    ];
    setItems(fetchedItems);
  }, []);

  // const handleDeleteItem = (itemId) => {
  //   setItems((prevItems) =>
  //     prevItems.filter((item) => item.id !== itemId)
  //   );
  // };

  const handleDeleteItem = async (itemId) => {
    try {
      const response = await fetch(`http://localhost:3001/items/${itemId}`, {
        method: "DELETE",
      });
      if (response.ok) {
        setItems((prevItems) =>
          prevItems.filter((item) => item.id !== itemId)
        );
      } else {
        console.error("Error deleting item:", response.status);
      }
    } catch (error) {
      console.error("Error deleting item:", error);
    }
  };

  return (
    <div className="items">
      {items.map((item) => (
        <Item
          id={item.id}
          name={item.name}
          description={item.description}
          price={item.price}
          image={item.image}
          size={item.size}
          onDelete={() => handleDeleteItem(item.id)}
        ></Item>
      ))}
    </div>
  );
};

export default Items;
