import React, { useState, useEffect } from 'react';
import "./FilteredItems.css";

const FilteredItems = () => {
  const [items, setItems] = useState([]);
  const [filteredItems, setFilteredItems] = useState([]);
  const [selectedSize, setSelectedSize] = useState('');

  useEffect(() => {
    fetchItems();
  }, []);

  const fetchItems = async () => {
    try {
      const url = selectedSize ? `http://localhost:3001/items?size=${selectedSize}` : 'http://localhost:3001/items';
      const response = await fetch(url);
      if (response.ok) {
        const data = await response.json();
        setItems(data);
      } else {
        console.error('Error fetching items:', response.status);
      }
    } catch (error) {
      console.error('Error fetching items:', error);
    }
  };

  const handleFilter = () => {
    fetchItems();
  };

  return (
    <div className="filtered-items">
      <div className="filter-container">
        <label className="filter-label">Filter by Size:</label>
        <select
          className="filter-select"
          value={selectedSize}
          onChange={(e) => setSelectedSize(e.target.value)}
        >
          <option value="">All</option>
          <option value="small">Small</option>
          <option value="medium">Medium</option>
          <option value="large">Large</option>
        </select>
        <button className="filter-button" onClick={handleFilter}>
          Filter
        </button>
      </div>

      <ul className="item-list">
        {filteredItems.length > 0 ? (
          filteredItems.map((item) => (
            <li className="item" key={item.id}>
              <div className="item-name">
                <h2>{item.name}</h2>
                <img className="item-image" src={item.image} alt={item.name} />
              </div>
              <div className="item-details">
                <div className="item-price">${item.price}</div>
                <div>{item.description}</div>
              </div>
            </li>
          ))
        ) : (
          <li className="no-items">No items found</li>
        )}
      </ul>
    </div>
  );
};

export default FilteredItems;
